import React,{useState} from "react";

function App(){
  const[bills,setBills]=useState([]);

  const addBill=(newBill)=>{
    setBills([...bills,{id:bills.length+1,...newBill}]);
  };

  return(
    <div>
      <h1>Billing App</h1>
      <AddNewBill onAddBill={addBill}/>
    </div>

  );
}

function AddNewBill({onAddBill}){
  const[description,setDescription]=useState("");
  const[cost,setCost]=useState("");
  const[qty,setQty]=useState();
  const handleAdd=()=>{
    if(description && cost && qty){
      onAddBill({
        description,
        cost:parseFloat(cost),
        qty:parseInt(qty,10),
        total:parseFloat(cost)*parseInt(qty,10),
      });
      setDescription("");
      setCost("");
      setQty("");
    }
  };

      return(
        <div>
          <h3>Add New Bill</h3>
          <input type="text" placeholder="Description" value={description}
          onChange={(e)=>setDescription(e.target.value)}
          />

          <input type="number" placeholder="Qty" value={qty}
          onChange={(e)=>setQty(e.target.value)}
          />

<input type="number" placeholder="cost" value={cost}
          onChange={(e)=>setCost(e.target.value)}
          />

          <button onClick ={handleAdd}>Add</button>


        </div>
      )



}
export default App;
