import React, { useState } from 'react';

const DictionaryExample = () => {
  const dictionary = {
    apple: "Red in color",
    banana: "Yellow in color",
    cherry: "Red in color",
  };

  const [selectedKey, setSelectedKey] = useState("apple"); // Default to 'apple'

  const handleSelectionChange = (event) => {
    setSelectedKey(event.target.value); // Update the selected key
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h1>Dictionary</h1>

      {/* Dropdown for selecting an option */}
      <select value={selectedKey} onChange={handleSelectionChange}>
        {Object.keys(dictionary).map((key) => (
          <option key={key} value={key}>
            {key}
          </option>
        ))}
      </select>

      {/* Display the selected option and its description */}
      <p>
        <strong>{selectedKey}:</strong> {dictionary[selectedKey]}
      </p>
    </div>
  );
};

export default DictionaryExample;



