import React, { useState } from "react";

function App() {
  const [bills, setBills] = useState([]);

  const addBill = (newBill) => {
    setBills([...bills, { id: bills.length + 1, ...newBill }]);
  };

  return (
    <div>
      <h1>Billing App</h1>
      <AddNewBill onAddBill={addBill} />
      <h2>Bill List</h2>
      <ul>
        {bills.map((bill) => (
          <li key={bill.id}>
            <strong>{bill.description}</strong>: Qty {bill.qty}, Cost ${bill.cost.toFixed(2)}, Total ${bill.total.toFixed(2)}
          </li>
        ))}
      </ul>
    </div>
  );
}

function AddNewBill({ onAddBill }) {
  const [description, setDescription] = useState("");
  const [cost, setCost] = useState("");
  const [qty, setQty] = useState("");

  const handleAdd = () => {
    if (description && cost && qty) {
      onAddBill({
        description,
        cost: parseFloat(cost),
        qty: parseInt(qty, 10),
        total: parseFloat(cost) * parseInt(qty, 10),
      });
      setDescription("");
      setCost("");
      setQty("");
    } else {
      alert("Please fill in all fields.");
    }
  };

  return (
    <div>
      <h3>Add New Bill</h3>
      <input
        type="text"
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <input
        type="number"
        placeholder="Qty"
        value={qty}
        onChange={(e) => setQty(e.target.value)}
      />
      <input
        type="number"
        placeholder="Cost"
        value={cost}
        onChange={(e) => setCost(e.target.value)}
      />
      <button onClick={handleAdd}>Add</button>
    </div>
  );
}

export default App;
