import React, { useState } from "react";

function BillingApp() {
  const [bills, setBills] = useState([]);
  const [description, setDescription] = useState("");
  const [cost, setCost] = useState("");
  const [qty, setQty] = useState("");
  const [searchQuery, setSearchQuery] = useState("");

  const addBill = () => {
    if (description && cost && qty) {
      const total = parseInt(cost) * parseInt(qty);
      const newBill = {
        id: bills.length + 1,
        description,
        qty: parseInt(qty),
        cost: parseInt(cost),
        total,
      };
      setBills([...bills, newBill]);
      setDescription("");
      setCost("");
      setQty("");
    }
  };

  const filteredBills = searchQuery
    ? bills.filter((bill) =>
        bill.description.toLowerCase().includes(searchQuery.toLowerCase())
      )
    : bills;

  const totalAmount = filteredBills.reduce((sum, bill) => sum + bill.total, 0);

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif", backgroundColor: "#f4f4f9" }}>
      <h2 style={{ color: "#2c3e50", textAlign: "center", fontSize: "36px" }}>Billing App</h2>

      <div
        style={{
          marginBottom: "20px",
          border: "1px solid #f39c12",
          padding: "15px",
          backgroundColor: "#ffeb3b",
          borderRadius: "8px",
          boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
        }}
      >
        <h3 style={{ fontSize: "24px", color: "#2c3e50", marginBottom: "10px" }}>Add New Bill</h3>
        <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "10px" }}>
          <input
            type="text"
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            style={{
              padding: "8px",
              width: "30%",
              borderRadius: "4px",
              border: "1px solid #ccc",
              fontSize: "16px",
            }}
          />
          <input
            type="number"
            placeholder="Cost"
            value={cost}
            onChange={(e) => setCost(e.target.value)}
            style={{
              padding: "8px",
              width: "20%",
              borderRadius: "4px",
              border: "1px solid #ccc",
              fontSize: "16px",
            }}
          />
          <input
            type="number"
            placeholder="Qty"
            value={qty}
            onChange={(e) => setQty(e.target.value)}
            style={{
              padding: "8px",
              width: "20%",
              borderRadius: "4px",
              border: "1px solid #ccc",
              fontSize: "16px",
            }}
          />
          <button
            onClick={addBill}
            style={{
              padding: "8px 20px",
              backgroundColor: "#2ecc71",
              color: "white",
              border: "none",
              borderRadius: "4px",
              fontSize: "16px",
              cursor: "pointer",
              alignSelf: "center",
            }}
          >
            Add
          </button>
        </div>
      </div>

      <div
        style={{
          marginBottom: "20px",
          padding: "10px",
          backgroundColor: "#e74c3c",
          color: "white",
          borderRadius: "8px",
          boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
        }}
      >
        <h3 style={{ fontSize: "24px", color: "white", marginBottom: "10px" }}>Search Bill</h3>
        <input
          type="text"
          placeholder="Search by description"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{
            padding: "8px",
            width: "100%",
            borderRadius: "4px",
            border: "1px solid #ccc",
            fontSize: "16px",
          }}
        />
      </div>

      <div
        style={{
          marginBottom: "20px",
          border: "1px solid #8e44ad",
          padding: "15px",
          borderRadius: "8px",
          boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
        }}
      >
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr style={{ backgroundColor: "#8e44ad", color: "white", textAlign: "left" }}>
              <th style={{ padding: "8px" }}>ID</th>
              <th style={{ padding: "8px" }}>Description</th>
              <th style={{ padding: "8px" }}>Qty</th>
              <th style={{ padding: "8px" }}>Cost</th>
              <th style={{ padding: "8px" }}>Total</th>
            </tr>
          </thead>
          <tbody>
            {filteredBills.map((bill) => (
              <tr key={bill.id}>
                <td style={{ padding: "8px", border: "1px solid #ccc" }}>{bill.id}</td>
                <td style={{ padding: "8px", border: "1px solid #ccc" }}>{bill.description}</td>
                <td style={{ padding: "8px", border: "1px solid #ccc" }}>{bill.qty}</td>
                <td style={{ padding: "8px", border: "1px solid #ccc" }}>${bill.cost}</td>
                <td style={{ padding: "8px", border: "1px solid #ccc" }}>${bill.total}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div
        style={{
          fontWeight: "bold",
          padding: "10px",
          backgroundColor: "#f39c12",
          color: "white",
          borderRadius: "8px",
          boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
        }}
      >
        <h3>Total: ${totalAmount}</h3>
      </div>
    </div>
  );
}

export default BillingApp;



