import React, { useState } from "react";

function BillingApp() {
  const [bills, setBills] = useState([]);
  const [description, setDescription] = useState("");
  const [cost, setCost] = useState("");
  const [qty, setQty] = useState("");
  const [searchQuery, setSearchQuery] = useState("");

  const addBill = () => {
    if (description && cost && qty) {
      const total = parseInt(cost) * parseInt(qty);
      const newBill = {
        id: bills.length + 1,
        description,
        qty: parseInt(qty),
        cost: parseInt(cost),
        total,
      };
      setBills([...bills, newBill]);
      setDescription("");
      setCost("");
      setQty("");
    }
  };

  const filteredBills = searchQuery
    ? bills.filter((bill) =>
        bill.description.toLowerCase().includes(searchQuery.toLowerCase())
      )
    : bills;

  const totalAmount = filteredBills.reduce((sum, bill) => sum + bill.total, 0);

  return (
    <div
      style={{
        padding: "20px",
        fontFamily: "cursive",
        backgroundColor: "#F5EFE7",
        color: "#213555",
        minHeight: "100vh",
      }}
    >
      <h2 style={{ textAlign: "center", color: "green" }}>Billing App</h2>

      <div
        style={{
          marginBottom: "20px",
          border: "2px solid #D8C4B6",
          borderRadius: "10px",
          padding: "15px",
          backgroundColor: "#FFFFFF",
          boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
        }}
      >
        <h3 style={{ marginBottom: "10px", color: "#3E5879" }}>Add New Bill</h3>
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          style={{
            marginRight: "10px",
            padding: "8px",
            border: "1px solid #D8C4B6",
            borderRadius: "5px",
            width: "200px",
          }}
        />
        <input
          type="number"
          placeholder="Cost"
          value={cost}
          onChange={(e) => setCost(e.target.value)}
          style={{
            marginRight: "10px",
            padding: "8px",
            border: "1px solid #D8C4B6",
            borderRadius: "5px",
            width: "100px",
          }}
        />
        <input
          type="number"
          placeholder="Qty"
          value={qty}
          onChange={(e) => setQty(e.target.value)}
          style={{
            marginRight: "10px",
            padding: "8px",
            border: "1px solid #D8C4B6",
            borderRadius: "5px",
            width: "100px",
          }}
        />
        <button
          onClick={addBill}
          style={{
            padding: "8px 15px",
            backgroundColor: "#3E5879",
            color: "#F5EFE7",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Add
        </button>
      </div>

      <div
        style={{
          marginBottom: "20px",
          border: "2px solid #D8C4B6",
          borderRadius: "10px",
          padding: "15px",
          backgroundColor: "#FFFFFF",
          boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
        }}
      >
        <h3 style={{ marginBottom: "10px", color: "#3E5879" }}>Search Bill</h3>
        <input
          type="text"
          placeholder="Description to search"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{
            padding: "8px",
            border: "1px solid #D8C4B6",
            borderRadius: "5px",
            width: "250px",
          }}
        />
      </div>

      <div
        style={{
          marginBottom: "20px",
          border: "2px solid #D8C4B6",
          borderRadius: "10px",
          padding: "15px",
          backgroundColor: "#FFFFFF",
          boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
        }}
      >
        <table
          style={{
            width: "100%",
            borderCollapse: "collapse",
            textAlign: "left",
            color: "#213555",
          }}
        >
          <thead>
            <tr style={{ backgroundColor: "#D8C4B6", color: "#FFFFFF" }}>
              <th style={{ padding: "10px", border: "1px solid #F5EFE7" }}>ID</th>
              <th style={{ padding: "10px", border: "1px solid #F5EFE7" }}>
                Description
              </th>
              <th style={{ padding: "10px", border: "1px solid #F5EFE7" }}>Qty</th>
              <th style={{ padding: "10px", border: "1px solid #F5EFE7" }}>Cost</th>
              <th style={{ padding: "10px", border: "1px solid #F5EFE7" }}>
                Total
              </th>
            </tr>
          </thead>
          <tbody>
            {filteredBills.map((bill) => (
              <tr key={bill.id}>
                <td style={{ padding: "8px", border: "1px solid #F5EFE7" }}>
                  {bill.id}
                </td>
                <td style={{ padding: "8px", border: "1px solid #F5EFE7" }}>
                  {bill.description}
                </td>
                <td style={{ padding: "8px", border: "1px solid #F5EFE7" }}>
                  {bill.qty}
                </td>
                <td style={{ padding: "8px", border: "1px solid #F5EFE7" }}>
                  {bill.cost}
                </td>
                <td style={{ padding: "8px", border: "1px solid #F5EFE7" }}>
                  {bill.total}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div
        style={{
          fontWeight: "bold",
          border: "2px solid #D8C4B6",
          borderRadius: "10px",
          padding: "15px",
          backgroundColor: "#FFFFFF",
          textAlign: "center",
          boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
        }}
      >
        Total: {totalAmount}
      </div>
    </div>
  );
}

export default BillingApp;




