import React, { useState } from "react";
import axios from "axios";

const App = () => {
  const [query, setQuery] = useState(""); // Search query
  const [results, setResults] = useState([]); // Search results
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(""); // Error message

  // Fetching API Key from environment variables
  const API_KEY = process.env.REACT_APP_GOOGLE_API_KEY;
  const SEARCH_ENGINE_ID = "YOUR_SEARCH_ENGINE_ID"; // Replace with your Search Engine ID

  const handleSearch = async (e) => {
    e.preventDefault(); // Prevent form submission
    setLoading(true);
    setError("");
    setResults([]);

    if (!query.trim()) {
      setError("Please enter a search term.");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.get(
        `https://www.googleapis.com/customsearch/v1?q=${query}&key=${API_KEY}&cx=${SEARCH_ENGINE_ID}`
      );

      console.log("API Response:", response.data); // Debugging: log the response

      if (response.data.items) {
        setResults(response.data.items);
      } else {
        setError("No results found.");
      }
    } catch (err) {
      console.error("Error fetching data:", err); // Debugging: log the error
      setError("Failed to fetch search results. Please try again.");
    }

    setLoading(false);
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Google Search Clone</h1>
      <form onSubmit={handleSearch} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search Google..."
          style={{
            padding: "10px",
            width: "80%",
            border: "1px solid #ccc",
            borderRadius: "4px",
          }}
        />
        <button
          type="submit"
          style={{
            padding: "10px 20px",
            marginLeft: "10px",
            backgroundColor: "#4CAF50",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
          }}
        >
          Search
        </button>
      </form>

      {loading && <p>Loading...</p>}

      {error && <p style={{ color: "red" }}>{error}</p>}

      <div>
        {results.map((result, index) => (
          <div
            key={index}
            style={{
              marginBottom: "20px",
              padding: "10px",
              border: "1px solid #ccc",
              borderRadius: "4px",
            }}
          >
            <a
              href={result.link}
              target="_blank"
              rel="noopener noreferrer"
              style={{ fontSize: "18px", fontWeight: "bold", color: "#1a0dab" }}
            >
              {result.title}
            </a>
            <p style={{ color: "#006621" }}>{result.link}</p>
            <p>{result.snippet}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;






