import React, { useState } from "react";
import { CgEnter } from "react-icons/cg";

function App() {
  const [bills, setBills] = useState([]);

  const addBill = (newBill) => {
    setBills([...bills, { id: bills.length + 1, ...newBill }]);
  };

  return (
    <div style={{textAlign:"center"}}>
      <h1 style={{color:'green', fontFamily:"cursive"}}>Billing App</h1>
      <AddNewBill onAddBill={addBill} />
      <Search bills={bills} />
    </div>
  );
}

function AddNewBill({ onAddBill }) {
  const [description, setDescription] = useState("");
  const [cost, setCost] = useState("");
  const [qty, setQty] = useState("");

  const handleAdd = () => {
    if (description && cost && qty) {
      onAddBill({
        description,
        cost: parseFloat(cost),
        qty: parseInt(qty, 10),
        total: parseFloat(cost) * parseInt(qty, 10),
      });
      setDescription("");
      setCost("");
      setQty("");
    }
  };

  return (
    <div>
      <h3>Add New Bill</h3>
      <input
        type="text"
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <input
        type="number"
        placeholder="Qty"
        value={qty}
        onChange={(e) => setQty(e.target.value)}
      />
      <input
        type="number"
        placeholder="Cost"
        value={cost}
        onChange={(e) => setCost(e.target.value)}
      />
      <button onClick={handleAdd}>Add</button>
    </div>
  );
}

function Search({ bills }) {
  const [searchTerm, setSearchTerm] = useState("");

  const filteredBills = bills.filter((bill) =>
    bill.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h3 style={{ color: 'red' }}>Search Bills</h3>


      <input
        type="text"
        placeholder="Search by description"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <ul>
        {filteredBills.map((bill) => (
          <li key={bill.id}>
            <strong>{bill.description}</strong>: Qty {bill.qty}, Cost $
            {bill.cost.toFixed(2)}, Total ${bill.total.toFixed(2)}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
