import React, { useState } from 'react';

const App = () => {
  const [query, setQuery] = useState(''); // Store search query
  const [results, setResults] = useState([]); // Store search results
  const [isLoading, setIsLoading] = useState(false); // Handle loading state
  const [error, setError] = useState(''); // Handle errors

  // Your Google Custom Search API Key and Search Engine ID
  const API_KEY = 'your_google_api_key'; // Replace with your API key
  const SEARCH_ENGINE_ID = 'your_search_engine_id'; // Replace with your search engine ID

  const handleSearch = async (e) => {
    e.preventDefault(); // Prevent form submission
    if (!query.trim()) return; // If query is empty, do nothing

    setIsLoading(true);
    setError('');
    setResults([]);

    try {
      // Fetch results from Google Custom Search API
      const response = await fetch(
        `https://www.googleapis.com/customsearch/v1?key=${API_KEY}&cx=${SEARCH_ENGINE_ID}&q=${query}`
      );

      if (!response.ok) {
        throw new Error('Failed to fetch search results.');
      }

      const data = await response.json();

      // Update results or show no results message
      setResults(data.items || []);
    } catch (err) {
      setError(err.message);
    }

    setIsLoading(false);
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Search Bar Clone</h1>
      <form onSubmit={handleSearch} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search Google..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          style={{
            padding: '10px',
            width: '300px',
            marginRight: '10px',
            border: '1px solid #ccc',
            borderRadius: '5px',
          }}
        />
        <button type="submit" style={{ padding: '10px 20px', cursor: 'pointer' }}>
          Search
        </button>
      </form>

      {isLoading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div>
        {results.length > 0 ? (
          results.map((result, index) => (
            <div key={index} style={{ marginBottom: '20px' }}>
              <h3>
                <a href={result.link} target="_blank" rel="noopener noreferrer">
                  {result.title}
                </a>
              </h3>
              <p>{result.snippet}</p>
            </div>
          ))
        ) : (
          query &&
          !isLoading &&
          !error && <p>No results found.</p>
        )}
      </div>
    </div>
  );
};

export default App;





